mapper.insert:
select LAST_INSERT_ROWID()
insert into chat_member(
) values ( #{id,jdbcType=INTEGER}, #{groupId,jdbcType=INTEGER}, #{memberId,jdbcType=INTEGER}, #{nickName,jdbcType=VARCHAR}, #{isBlocked,jdbcType=BOOLEAN}, #{lastCommand,jdbcType=VARCHAR}, #{level,jdbcType=INTEGER}, #{todayWelcome,jdbcType=BOOLEAN}, #{exp,jdbcType=BIGINT}, #{todaySign,jdbcType=BOOLEAN}, #{totalSign,jdbcType=BIGINT} )

mapper.select:
select * from chat_member
where group_id = #{groupId}
and member_id = #{memberId}
limit 1
